import React from 'react';
import './menu-item.style.scss'
const MenuItem=({title,imageUrl,size})=>(
    <div 
   //thats how we set a ling as src or url in style from props

    className={`${size} menu-item`}> {/*  dynamic size property to add to the className from props then we secify style for large classes in scss */}
    <div className="background-image" style={{
backgroundImage:`url(${imageUrl})`}}/>
    <div className="content">
<h1 className="title">{title.toUpperCase()}</h1>
<span className="subtitile">SHOP NOW</span>
    </div>
</div>
)
export default MenuItem
